import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function Edit() {

    const {id} = useParams(); //getting id from the link
    useEffect(() => {

    }, [])

    const [user,setUser] = useState({
        name:"",
        phone:"",
        email:"",
    })

    const handleChange = (e) => {
        setUser((prev) => ({...prev, [e.target.name]: e.target.value}))
    }

    const handleClick = async(e) => {
        e.preventdefault()
        try {
            await axios.update("http://localhost:3000/users", user)
        } catch(err) {
            console.log(err)
        }
    }

    return (
        <div>
            <h1>Edit User</h1>
            <div className="Form">
                <label>Name:</label>
                <input type="text" placeholder="Name" name="name" onChange={handleChange}></input>
                <label>Email:</label>
                <input type="text" placeholder="Email" name="email" onChange={handleChange}></input>
                <label>Phone:</label>
                <input type="text" placeholder="Phone" name="phone" onChange={handleChange}></input>
                <button onClick={handleClick}>Edit</button>
            </div>
        </div>
    )
}

export default Edit;
